import React, { useState, useEffect } from 'react'
import './ItemTable.css'
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import PreviewIcon from '@mui/icons-material/Preview';
import useItems from '../Context/StateContext';



function ItemTable() {

    const initialBasket = {
        outof_stock: 0,
        storeValue: 0,
        numItems: 0,
        darkMode: false,
        user: null,
    }

    const { items, dispatch } = useItems();
    //console.log(items) debugging
    console.log(items)

    let storeValue;
    useEffect(() => {
        const fetchItems = async () => {
            const response = await fetch('/api/items')
            const json = await response.json();
    
            if(response.ok){
                await dispatch({
                    type: 'SET_ITEMS',
                    payload: json,
                })

                // items.invenItems.forEach(e => {
                //     storeValue += e.price
                // });

                console.log(items.invenItems.length)
                for (let i = 0; i < items.invenItems.length; i++) {
                    const value = items.invenItems[i].price;
                    
                }
            }
        }
        fetchItems();
      }, []);

  return (
    <div className="datatable">
      <table>
        <thead>
            <tr>
                <th>ID</th>
                <th>Product Title</th>
                <th>Rating</th>
                <th>Price</th>
                <th>Weight</th>
                <th>Quantity</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
        {items ? items.invenItems.map(product => {
            const handleDelete = async () => {
                const response = await fetch('/api/items/' + product._id, {
                    method: 'DELETE'
                })
                const json = await response.json();

                if (response.ok) {
                    dispatch({
                        type: 'DELETE_ITEM',
                        item: {
                            id: product._id, 
                        }
                    })
                }
            }
            return (
                <tr>
                <td>1</td>
                <td>{product.title}</td>
                <td>{product.rating}</td>
                <td>${product.price}</td>
                <td>{product.weight}kg</td>
                <td>{product.quantity}</td>
                <td>
                    <PreviewIcon style={{color: 'crimson', margin: '0 3px'}} className='table-icons'/>
                    <EditIcon style={{color: 'purple', margin: '0 3px' }} className='table-icons'/>
                    <DeleteIcon style={{color: 'green', margin: '0 3px'}} className='table-icons' onClick={handleDelete}/>
                </td>
                <td>{product._id}</td>
                </tr>
            )}
        ) : (
            <h1>There are no items</h1>
        )}
        </tbody>
      </table>
    </div>
  )
}

export default ItemTable
