import React, { useState } from 'react'
import './Modal.css'
import  { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import useItems from '../Context/StateContext';
import axios from 'axios'
import { updateUserRoute } from '../ApiRoutes/ApiRoutes';
import { toastOptions } from '../utilities/toastOptions';

function Modal({ closeModal, email, username, newPassword}) {

    const [value, setValue] = useState("");
    const { items, dispatch } = useItems();

    console.log(updateUserRoute)
    console.log(email, username, newPassword, value)

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (value.length < 8) {
            toast.error("Password must have at least 8 characters", toastOptions)
        } else {
            await axios.put(`${updateUserRoute}/${eid}`, {
                username,
                email,
            })
            .then( res => {
                // console.log(res.data)
                dispatch({
                    type: 'SET_USER',
                    id: eid,
                    product: res.data.newUser,
                })
                toast.success("Successfully Updated!", toastOptions)
            })
            .catch( err => {
                console.log(err)
                toast.error("Cannot Edit", toastOptions)
            })
        }
    }

  return (
    <div className="modalBackground">
        <div className="modalOverlay"></div>
        <div className="modalContainer">
            <div className="modal-title">
                <p>Please Enter Your Password To Proceed</p>
            </div>
            <div className="modal-inputs">
                {/* <p>Password</p> */}
                <input type="text" onChange={e => setValue(e.target.value)} value={value} placeholder='Password'/>
            </div>
            <div className="modal-button">
                <button onClick={() => closeModal(false)}>Close</button>
                <button onClick={handleSubmit}>Continue</button>
            </div>
        </div>
        <ToastContainer />
    </div>
  )
}

export default Modal
