import React, { useState, useEffect } from 'react'
import  { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './EditUser.css'
import useItems from '../Context/StateContext';
import axios from 'axios'
import { updateUserRoute } from '../ApiRoutes/ApiRoutes';
import { toastOptions } from '../utilities/toastOptions';
import Modal from '../Modal/Modal';

function EditUser({ eid, eemail, eusername }) {

    const { items, dispatch } = useItems();

    const [open, setOpen] = useState(false);
    const [email, setEmail] = useState(eemail);
    const [username, setUsername] = useState(eusername);

    // console.log(`${updateItemRoute}/${eid}`)

    const handleSubmit = async (e) => {
        e.preventDefault();

        setOpen(true)

        // await axios.put(`${updateUserRoute}/${eid}`, {
        //     username,
        //     email,
        // })
        // .then( res => {
        //     // console.log(res.data)
        //     dispatch({
        //         type: 'SET_USER',
        //         id: eid,
        //         product: res.data.newUser,
        //     })
        //     toast.success("Successfully Updated!", toastOptions)
        // })
        // .catch( err => {
        //     console.log(err)
        //     toast.error("Cannot Edit", toastOptions)
        // })
    }

  return (
    <div className="top-container">
        <div className="top">
              <h2>Edit Admin Details</h2>
        </div>
        <div className="bottom">
            <div className="right">
                <form>                    
                    <div className="formInput">
                        <label>Username</label>
                        <input type="text" requried placeholder={username} onChange={e => setUsername(e.target.value)} value={username} />
                    </div>
                    <div className="formInput">
                        <label>Email</label>
                        <input type="text" requried placeholder={email} onChange={e => setEmail(e.target.value)} value={email} />
                    </div>
                    <button type="submit" onClick={handleSubmit}>Save</button>
                </form>
            </div>
        </div>
        {
            open && (
                <Modal />
            )
        }
        <ToastContainer />
    </div>
  )
}

export default EditUser
