import React from 'react'
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import PreviewIcon from '@mui/icons-material/Preview';
import './ItemTable.css';
import useItems from '../Context/StateContext';

function ProductRow({ id, title, rating, weight, price, quantity}) {

    const { items, dispatch } = useItems();

    const handleDelete = async () => {
        const response = await fetch('/api/items/' + product._id, {
            method: 'DELETE'
        })
        const json = await response.json();

        if (response.ok) {
            dispatch({
                type: 'DELETE_ITEM',
                item: {
                    id: product._id, 
                }
            })
        }
    }   

  return (
        <tr>
            <td>1</td>
            <td>{title}</td>
            <td>{rating}</td>
            <td>${price}</td>
            <td>{weight}kg</td>
            <td>{quantity}</td>
            <td>
                <PreviewIcon style={{color: 'crimson', margin: '0 3px'}} className='table-icons'/>
                <EditIcon style={{color: 'purple', margin: '0 3px' }} className='table-icons'/>
                <DeleteIcon style={{color: 'green', margin: '0 3px'}} className='table-icons' onClick={handleDelete}/>
            </td>
            <td>{id}</td>
        </tr>
  )
}

export default ProductRow
