import React, { useState, useEffect } from 'react'
import './ItemTable.css'
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import PreviewIcon from '@mui/icons-material/Preview';
import useItems from '../Context/StateContext';


function ItemTable() {

    const { items, dispatch } = useItems();
    //console.log(items) debugging

    const [products, setProducts] = useState(null);

    useEffect(() => {
        const fetchItems = async () => {
            const response = await fetch('/api/items')
            const json = await response.json();

            if(response.ok){
                await setProducts(json);
                if (products && products !== null) {
                        products.map(item => {
                            dispatch({
                                type: 'ADD_ITEM',
                                product: {
                                    id: item.id,
                                    title: item.title,
                                    img: item.img,
                                    rating: item.rating,
                                    price: item.price,
                                    weight: item.weight,
                                    quantity: item.quantity,
                                }
                            })
                        })
                    }
                
                }
            }
        fetchItems();
    }, []);

    const handleDelete = async (id) => {
        console.log(id);
        // const response = await fetch('/api/items' + id, { 
        //     method: 'DELETE',

        // })
        // const json = await response.json();
        // if(response.ok){
        //     dispatch({
        //         type: 'DELETE_ITEM',
        //         item: {
        //             id: id
        //         }
        //     })
        // }
    }

  return (
    <div className="datatable">
      <table>
        <thead>
            <tr>
                <th>ID</th>
                <th>Product Title</th>
                <th>Rating</th>
                <th>Price</th>
                <th>Weight</th>
                <th>Quantity</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
        {products && products.map(product => 
            <tr>
                <td>1</td>
                <td>{product.title}</td>
                <td>{product.rating}</td>
                <td>${product.price}</td>
                <td>{product.weight}kg</td>
                <td>{product.quantity}</td>
                <td>
                    <PreviewIcon style={{color: 'crimson', margin: '0 3px'}} className='table-icons'/>
                    <EditIcon style={{color: 'purple', margin: '0 3px' }} className='table-icons'/>
                    <DeleteIcon style={{color: 'green', margin: '0 3px'}} className='table-icons' onClick={handleDelete}/>
                </td>
                <td>{product._id}</td>
            </tr>
        )}
        </tbody>
      </table>
    </div>
  )
}

export default ItemTable
