import React from 'react'
import Layout from '../Layout/Layout'
import './EditItem.css'
import ProductDetails from '../Product_Details/ProductDetails'
import useItems from '../Context/StateContext'
import { useParams } from "react-router-dom";

function EditItem() {

    const { items, dispatch } = useItems();
    const routeParams = useParams();

    const getItem = items.invenItems[routeParams.id].itemDetails;
    console.log(getItem)

  return (
    <>
        <Layout>
            <div className="item-details">
                <div className="item-header">
                    <h1>Product Details</h1>
                </div>
                <div className="item-info">
                    {
                        items.invenItems.map((item) => {
                            return (
                                <ProductDetails 
                                    key={item._id}
                                    id={item._id}
                                    title={item.itemDetails.title}
                                    desc={item.itemDetails.desc}
                                    imageURL={item.itemDetails.imageURL}
                                    price={item.itemDetails.price}
                                    rating={item.itemDetails.rating}
                                    quantity={item.itemDetails.quantity}
                                    weight={item.itemDetails.weight}
                                />  
                            )
                        })
                    }
                </div>
            </div>
        </Layout>
    </>
  )
}

export default EditItem
