import React, { useState, useEffect } from 'react'
import './ItemTable.css'
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import PreviewIcon from '@mui/icons-material/Preview';
import useItems from '../Context/StateContext';
import ProductRow from './ProductRow.js';
import axios from 'axios';
import { getAllItemsRoute } from '../ApiRoutes/ApiRoutes';

function ItemTable() {

    const { items, dispatch } = useItems();

    const storeValue = [];
    if(items.invenItems.length > 0){
      items.invenItems.forEach((item) => {
        storeValue.push((item.itemDetails.price * item.itemDetails.quantity))
      });
    }
    // console.log(storeValue)
    let subTotal;
    storeValue.forEach((item) => {
      subTotal += item;
    })
    console.log(subTotal)
    // useEffect(() => {

    // }, [items])

  return (
    <div className="datatable">
      <table>
        <thead>
            <tr>
                <th>ID</th>
                <th>Product Title</th>
                <th>Rating</th>
                <th>Price</th>
                <th>Weight</th>
                <th>Quantity</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
        {items.invenItems ? items.invenItems.map((item, index) => 
            <ProductRow 
                index={index}
                key={item._id}
                id={item._id}
                title={item.itemDetails.title}
                rating={item.itemDetails.rating}
                weight={item.itemDetails.weight}
                price={item.itemDetails.price}
                quantity={item.itemDetails.quantity}
            />
        ) : (
            <h1>There are no items</h1>
        )}
        </tbody>
      </table>
    </div>
  )
}

export default ItemTable
