import React, { useState } from 'react'
import './Login.css'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
import { loginUserRoute } from '../ApiRoutes/ApiRoutes';
import useItems from '../Context/StateContext';
import  { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Login() {

  const navigate = useNavigate();
  const { items, dispatch } = useItems();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const toastOptions = {
    position: "top-right",
    autoClose: 8000,
    pauseOnFocusHover: true,
    draggable: true,
    theme: 'dark',
  }

  const handleSignIn = async (e) => {
    e.preventDefault();

    const response = await axios.post(loginUserRoute, {
      email,
      password
    })

    if (response.data.status == true ) {
        dispatch({
          type: 'SET_USER',
          user: response.data.emailCheck
        })
        toast.success('Successfully Logged In!', toastOptions);
    } else {
      toast.error('Error logging in', toastOptions);
    }
  }

  return (
    <div className='container'>
      <div className="login-container">
        <div className="login-title">
          <p>Cellar's Admin</p>
        </div>
        <div className="login-box">
          <p className='login-box-title'>Welcome, admin!</p>
          <div className="login-inputs">
            <input type="text" placeholder='EMAIL' value={email} onChange={e => setEmail(e.target.value)} />
            <input type="password" placeholder='PASSWORD' value={password} onChange={e => setPassword(e.target.value)}/>
            <button onClick={handleSignIn}>Sign in</button>
          </div>
          <div className="login-register">
            <p onClick={() => navigate('/register')}>Create an account here</p>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Login
